n = [0];k = [1];p = 0.3;nl = 0;kl = 1;for i = 0:100        nl += 0.01;        n = [n,nl];        %kl = p*exp(-kl/(p*nl)) - (1-p)*exp(-kl/((1-p)*nl*2));        k2=p*exp(-kl/(p*nl));        k3 = (1-p)*exp(-kl/((1-p)*nl*2));                kl = k2-k3;        if (i==0)          disp(k2);          disp(k3);          disp(kl);      endif           k  = [k,kl]; endfor plot(n,k); set(gca, 'FontName', 'Arial')set(gca, 'FontSize', 40)ylabel('Miss Rate (m)')xlabel('''Array Size/Cache Size (w)');